pragma solidity >=0.4.21<0.6.0;
//pragma experimental ABIEncoderV2;

import "zeppelin-solidity/contracts/token/ERC20/ERC20Basic.sol";

contract TokenControl {
    constructor() public {}

    function transfer(address _token, address _to, uint256 _amount) public returns (bool success) {
        ERC20Basic(_token).transfer(_to, _amount);
        return true;

    }

    function balanceOf(address _token, address _account) external view returns (uint256 balance) {
        return ERC20Basic(_token).balanceOf(_account);
    }

}
